import React, { useRef, useEffect, useState } from 'react';
import { initializeApp } from 'firebase/app';
import { getDatabase, ref, onValue, set } from 'firebase/database';
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';

// Firebase configuration
const firebaseConfig = {
  apiKey: "AIzaSyAUjjIy9Sd5r8T5nxIinHbcseCvNHKKag4",
  authDomain: "schoolbus-led.firebaseapp.com",
  databaseURL: "https://schoolbus-led-default-rtdb.firebaseio.com",
  projectId: "schoolbus-led",
  storageBucket: "schoolbus-led.appspot.com",
  messagingSenderId: "1056264797823",
  appId: "YOUR_APP_ID" // Replace with your app ID from Firebase project settings
};

// Initialize Firebase
const app = initializeApp(firebaseConfig);
const database = getDatabase(app);

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [username, setUsername] = useState('');
  const [email, setEmail] = useState(''); // Store email to determine path
  const [startingAisle, setStartingAisle] = useState('');
  const [selectedItem, setSelectedItem] = useState('');
  const [showDropdown, setShowDropdown] = useState(false);
  const canvasRef = useRef(null);
  const aisleRefs = useRef({});
  const [rfidPath, setRfidPath] = useState('rfid-reader'); // Default path

  // Handle login form submission
  const handleLogin = (e) => {
    e.preventDefault();
    const formData = new FormData(e.target);
    const username = formData.get('username');
    const email = formData.get('email'); // Get email from form data
    setIsLoggedIn(true);
    setUsername(username);
    setEmail(email);

    // Determine the Firebase path based on email
    let path = 'rfid-reader'; // Default path
    if (email === 'member1@gmail.com') {
      path = 'rfid-reader';
    } else if (email === 'member2@gmail.com') {
      path = 'rfid-reader2';
    }

    setRfidPath(path);

    // Set the current user and Firebase path
    const userRef = ref(database, 'current-user');
    set(userRef, { username, rfidPath: path });
  };

  // Handle logout
  const handleLogout = () => {
    setIsLoggedIn(false);
    setUsername('');
    setEmail('');

    // Clear the current user from Firebase
    const userRef = ref(database, 'current-user');
    set(userRef, { username: '', rfidPath: '' });
  };

  // Use effect to get the path from Firebase when logged in
  useEffect(() => {
    if (isLoggedIn) {
      const userRef = ref(database, 'current-user');
      onValue(userRef, (snapshot) => {
        const userData = snapshot.val();
        if (userData) {
          const path = userData.rfidPath || 'rfid-reader';
          setRfidPath(path);
        }
      });
    }
  }, [isLoggedIn]);

  // Use effect to read starting aisle from Firebase
  useEffect(() => {
    if (isLoggedIn) {
      const rfidRef = ref(database, rfidPath);
      const rfidUnsubscribe = onValue(rfidRef, (snapshot) => {
        const readerData = snapshot.val();
        if (readerData) {
          setStartingAisle(readerData);
          setShowDropdown(true); // Show the dropdown when data is loaded
        }
      });

      return () => {
        rfidUnsubscribe();
      };
    }
  }, [isLoggedIn, rfidPath]);

  // Use effect to draw the path on canvas when starting aisle and selected item are set
  useEffect(() => {
    if (startingAisle && selectedItem && canvasRef.current) {
      drawPath();
    } else {
      clearPath(); // Clear path if one of the required values is not set or canvas is not ready
    }
  }, [startingAisle, selectedItem, canvasRef.current]);

  // Function to draw path on canvas
  const drawPath = () => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    const supermarket = document.getElementById('supermarket');
    const aisleElement = aisleRefs.current[startingAisle];
    const itemElement = document.getElementById(`item${selectedItem.replace('.', '-')}`);

    if (!canvas || !ctx || !aisleElement || !itemElement) return;

    ctx.clearRect(0, 0, canvas.width, canvas.height); // Clear previous drawings

    const aisleRect = aisleElement.getBoundingClientRect();
    const itemRect = itemElement.getBoundingClientRect();
    const supermarketRect = supermarket.getBoundingClientRect();

    const aisleX = aisleRect.left - supermarketRect.left + (aisleElement.offsetWidth / 2);
    const aisleY = aisleRect.top - supermarketRect.top + (aisleElement.offsetHeight / 2);
    const itemX = itemRect.left - supermarketRect.left + (itemElement.offsetWidth / 2);
    const itemY = itemRect.top - supermarketRect.top + (itemElement.offsetHeight / 2);

    ctx.beginPath();
    ctx.moveTo(aisleX, aisleY);
    ctx.lineTo(itemX, itemY);
    ctx.strokeStyle = '#e74c3c';
    ctx.lineWidth = 3;
    ctx.stroke();
  };

  // Function to clear the canvas
  const clearPath = () => {
    const canvas = canvasRef.current;
    const ctx = canvas?.getContext('2d');

    if (!canvas || !ctx) return;

    ctx.clearRect(0, 0, canvas.width, canvas.height); // Clear the canvas
  };

  // Handle reading starting aisle from Firebase and pushing "YES" to a different path
  const handleReadStartingAisle = () => {
    const rfidRef = ref(database, rfidPath);
    onValue(rfidRef, (snapshot) => {
      const readerData = snapshot.val();
      if (readerData) {
        setStartingAisle(readerData);
        setShowDropdown(false); // Hide the dropdown after reading the aisle

        // Push "YES" to Firebase path
        const pathRef = ref(database, 'find-aisle');
        set(pathRef, 'YES');
      }
    });
  };

  // Handle item click to set selected item
  const handleItemClick = (item) => {
    setSelectedItem(item);
    setShowDropdown(false);
  };

  return (
    <div className="App">
      <header className="App-header">
        {!isLoggedIn ? (
          <form onSubmit={handleLogin}>
            <input type="text" name="username" placeholder="Username" required />
            <input type="email" name="email" placeholder="Email" required />
            <button type="submit">Login</button>
          </form>
        ) : (
          <div>
            <p>Welcome, {username}!</p>
            <button onClick={handleLogout}>Logout</button>

            <button onClick={handleReadStartingAisle}>Read Starting Aisle</button>

            {showDropdown && (
              <div className="dropdown">
                <p>Starting Aisle: {startingAisle}</p>
                <ul>
                  {['1.1', '1.2', '1.3', '2.1', '2.2', '2.3', '3.1', '3.2', '3.3'].map((item) => (
                    <li key={item} onClick={() => handleItemClick(item)}>Item {item}</li>
                  ))}
                </ul>
              </div>
            )}

            <button onClick={drawPath}>Find Item</button>

            <div id="supermarket" style={{ position: 'relative', width: '600px', height: '400px', border: '1px solid #ccc', margin: '20px auto', display: 'grid', gridTemplateColumns: 'repeat(3, 1fr)', gridGap: '10px', backgroundColor: '#fafafa' }}>
              <div
                id="aisle1"
                className="aisle"
                ref={(el) => (aisleRefs.current['1'] = el)}
                style={{ backgroundColor: '#f4f4f4', position: 'relative' }}
              >
                <div className="item" style={{ top: '20px', left: '20px' }} id="item1-1"></div>
                <div className="item" style={{ top: '100px', left: '20px' }} id="item1-2"></div>
                <div className="item" style={{ top: '180px', left: '20px' }} id="item1-3"></div>
              </div>
              <div
                id="aisle2"
                className="aisle"
                ref={(el) => (aisleRefs.current['2'] = el)}
                style={{ backgroundColor: '#f4f4f4', position: 'relative' }}
              >
                <div className="item" style={{ top: '20px', left: '20px' }} id="item2-1"></div>
                <div className="item" style={{ top: '100px', left: '20px' }} id="item2-2"></div>
                <div className="item" style={{ top: '180px', left: '20px' }} id="item2-3"></div>
              </div>
              <div
                id="aisle3"
                className="aisle"
                ref={(el) => (aisleRefs.current['3'] = el)}
                style={{ backgroundColor: '#f4f4f4', position: 'relative' }}
              >
                <div className="item" style={{ top: '20px', left: '20px' }} id="item3-1"></div>
                <div className="item" style={{ top: '100px', left: '20px' }} id="item3-2"></div>
                <div className="item" style={{ top: '180px', left: '20px' }} id="item3-3"></div>
              </div>
            </div>

            <canvas ref={canvasRef} width="600" height="400" style={{ border: '1px solid #ccc' }}></canvas>
          </div>
        )}
      </header>
    </div>
  );
}

export default App;
