// Import necessary modules
import React, { useState, useEffect } from 'react';
import firebase from 'firebase/app';
import 'firebase/auth';
import 'firebase/database';
import 'bootstrap/dist/css/bootstrap.min.css';

// Firebase configuration (replace with your own)
const firebaseConfig = {
  apiKey: "AIzaSyAUjjIy9Sd5r8T5nxIinHbcseCvNHKKag4",
  authDomain: "schoolbus-led.firebaseapp.com",
  databaseURL: "https://schoolbus-led-default-rtdb.firebaseio.com",
  projectId: "schoolbus-led",
  storageBucket: "schoolbus-led.appspot.com",
  messagingSenderId: "1056264797823",
  appId: "1:1056264797823:web:ab123cd456ef7890ghij"
};

// Initialize Firebase
if (!firebase.apps.length) {
  firebase.initializeApp(firebaseConfig);
} else {
  firebase.app();
}

const App = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [user, setUser] = useState(null);
  const [uid, setUID] = useState('');

  useEffect(() => {
    const unsubscribe = firebase.auth().onAuthStateChanged(setUser);
    return () => unsubscribe();
  }, []);

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      await firebase.auth().signInWithEmailAndPassword(email, password);
    } catch (error) {
      console.error('Login failed', error);
    }
  };

  const handleUIDChange = (e) => {
    setUID(e.target.value);
  };

  const handleSubmitUID = async (e) => {
    e.preventDefault();
    if (user) {
      const currentUserEmail = user.email;
      try {
        // Get the accepted UID from Firebase
        const snapshot = await firebase.database().ref('users/' + currentUserEmail.replace('.', ',')).once('value');
        const acceptedUID = snapshot.val()?.acceptedUID;

        if (acceptedUID && acceptedUID === uid) {
          alert('UID authorized!');
        } else {
          alert('UID not authorized.');
        }
      } catch (error) {
        console.error('Error fetching UID', error);
      }
    } else {
      alert('You need to log in first.');
    }
  };

  return (
    <div className="container mt-5">
      <h1>Login</h1>
      {!user ? (
        <form onSubmit={handleLogin} className="mb-3">
          <div className="form-group">
            <label>Email</label>
            <input
              type="email"
              className="form-control"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
          </div>
          <div className="form-group">
            <label>Password</label>
            <input
              type="password"
              className="form-control"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
          </div>
          <button type="submit" className="btn btn-primary">Login</button>
        </form>
      ) : (
        <div>
          <h2>Welcome, {user.email}</h2>
          <form onSubmit={handleSubmitUID}>
            <div className="form-group">
              <label>Enter UID</label>
              <input
                type="text"
                className="form-control"
                value={uid}
                onChange={handleUIDChange}
                required
              />
            </div>
            <button type="submit" className="btn btn-primary">Submit UID</button>
          </form>
          <button className="btn btn-secondary mt-3" onClick={() => firebase.auth().signOut()}>Logout</button>
        </div>
      )}
    </div>
  );
};

export default App;
