import React, { useState, useEffect } from 'react';
import { initializeApp } from 'firebase/app';
import { getDatabase, ref, onValue, set } from 'firebase/database';
import { getStorage, ref as storageRef, listAll, getDownloadURL } from 'firebase/storage';
import { GoogleMap, LoadScript, Marker, InfoWindow } from '@react-google-maps/api';
import { saveAs } from 'file-saver';
import './App.css';

// Firebase configuration
const firebaseConfig = {
  apiKey: "AIzaSyAUjjIy9Sd5r8T5nxIinHbcseCvNHKKag4",
  authDomain: "schoolbus-led.firebaseapp.com",
  databaseURL: "https://schoolbus-led-default-rtdb.firebaseio.com",
  projectId: "schoolbus-led",
  storageBucket: "schoolbus-led.appspot.com",
  messagingSenderId: "1056264797823",
  appId: "YOUR_APP_ID" // Replace with your app ID from Firebase project settings
};

// Initialize Firebase
const app = initializeApp(firebaseConfig);
const database = getDatabase(app);
const storage = getStorage(app);

// Default center location
const center = {
  lat: 3.002912,
  lng: 101.464806
};

function App() {
  const [ledStatus, setLedStatus] = useState('OFF');
  const [mplData, setMplData] = useState({});
  const [gpsData, setGpsData] = useState({});
  const [imageUrls, setImageUrls] = useState([]);
  const [map, setMap] = useState(null);
  const [activeMarker, setActiveMarker] = useState(null);

  useEffect(() => {
    const ledRef = ref(database, 'test-led');
    const mplRef = ref(database, 'mpl');
    const gpsRef = ref(database, 'gps');

    const ledUnsubscribe = onValue(ledRef, (snapshot) => {
      const status = snapshot.val();
      setLedStatus(status);
    });

    const mplUnsubscribe = onValue(mplRef, (snapshot) => {
      const data = snapshot.val();
      setMplData(data);
    });

    const gpsUnsubscribe = onValue(gpsRef, (snapshot) => {
      const data = snapshot.val();
      setGpsData(data);
    });

    // Fetch specific images from Firebase Storage
    const fetchImages = async () => {
      try {
        const specificImages = [
          'images/photo_1_2024-08-23_12-18-04.jpg',
          'images/photo_2_2024-08-23_12-18-04.jpg',
          'images/photo_3_2024-08-23_12-18-04.jpg'
        ];

        const urls = await Promise.all(
          specificImages.map((image) => getDownloadURL(storageRef(storage, image)))
        );

        setImageUrls(urls);
      } catch (error) {
        console.error("Error fetching images:", error);
      }
    };

    fetchImages();

    return () => {
      ledUnsubscribe();
      mplUnsubscribe();
      gpsUnsubscribe();
    };
  }, []);

  const toggleLed = () => {
    const newStatus = ledStatus === 'ON' ? 'OFF' : 'ON';
    set(ref(database, 'test-led'), newStatus);
  };

  const handleMapLoad = (map) => {
    setMap(map);
  };

  const handleMarkerClick = () => {
    setActiveMarker({ lat: parseFloat(gpsData.latitude), lng: parseFloat(gpsData.longitude) });
  };

  const saveDataToFile = () => {
    const data = `
      LED Status: ${ledStatus}

      GPS Data:
      Latitude: ${gpsData.latitude || 'N/A'}
      Longitude: ${gpsData.longitude || 'N/A'}
      Speed: ${gpsData.speedKph || 'N/A'} km/h
      Altitude: ${gpsData.altitude || 'N/A'} m
      Timestamp: ${gpsData.timestamp ? new Date(gpsData.timestamp).toLocaleString() : 'N/A'}

      MPL Data:
      Altitude: ${mplData.altitude || 'N/A'}
      Pressure: ${mplData.pressure || 'N/A'}
      Temperature (C): ${mplData.temperatureC || 'N/A'}
      Temperature (F): ${mplData.temperatureF || 'N/A'}
      Timestamp: ${mplData.timestamp ? new Date(mplData.timestamp).toLocaleString() : 'N/A'}
    `;

    const blob = new Blob([data], { type: 'text/plain;charset=utf-8' });
    saveAs(blob, 'data.txt');
  };

  return (
    <div className="container">
      <h1 className="status-text">LED is {ledStatus}</h1>
      <div 
        className={`switch ${ledStatus === 'ON' ? 'switch-on' : 'switch-off'}`} 
        onClick={toggleLed}
      >
        <div className={`circle ${ledStatus === 'ON' ? 'circle-on' : 'circle-off'}`} />
      </div>

      <div className="data-section-container">
        <div className="data-section">
          <h2>GPS Data</h2>
          <p><strong>Latitude:</strong> {gpsData.latitude || 'N/A'}</p>
          <p><strong>Longitude:</strong> {gpsData.longitude || 'N/A'}</p>
          <p><strong>Speed:</strong> {gpsData.speedKph || 'N/A'} km/h</p>
          <p><strong>Altitude:</strong> {gpsData.altitude || 'N/A'} m</p>
          <p><strong>Timestamp:</strong> {gpsData.timestamp ? new Date(gpsData.timestamp).toLocaleString() : 'N/A'}</p>
        </div>

        <div className="data-section">
          <h2>MPL Data</h2>
          <p><strong>Altitude:</strong> {mplData.altitude || 'N/A'}</p>
          <p><strong>Pressure:</strong> {mplData.pressure || 'N/A'}</p>
          <p><strong>Temperature (C):</strong> {mplData.temperatureC || 'N/A'}</p>
          <p><strong>Temperature (F):</strong> {mplData.temperatureF || 'N/A'}</p>
          <p><strong>Timestamp:</strong> {mplData.timestamp ? new Date(mplData.timestamp).toLocaleString() : 'N/A'}</p>
        </div>
      </div>

      <button className="save-button" onClick={saveDataToFile}>Save</button>

      <div className="map-container">
        <h2>Map</h2>
        <LoadScript googleMapsApiKey="AIzaSyAYA_6-LODOtcvI0CfOBfuXGKPDd4MLzso">
          <GoogleMap
            id="map"
            mapContainerStyle={{ height: '50vh', width: '100%' }}
            center={gpsData.latitude && gpsData.longitude ? 
              { lat: parseFloat(gpsData.latitude), lng: parseFloat(gpsData.longitude) } : center}
            zoom={13}
            onLoad={handleMapLoad}
          >
            {gpsData.latitude && gpsData.longitude && (
              <Marker 
                position={{ lat: parseFloat(gpsData.latitude), lng: parseFloat(gpsData.longitude) }}
                icon={{
                  url: 'https://developers.google.com/maps/documentation/javascript/examples/full/images/beachflag.png',
                  scaledSize: new window.google.maps.Size(50, 50)
                }}
                onClick={handleMarkerClick}
              >
                {activeMarker && (
                  <InfoWindow position={activeMarker}>
                    <div>GPS Location</div>
                  </InfoWindow>
                )}
              </Marker>
            )}
          </GoogleMap>
        </LoadScript>
      </div>

      <div className="images-section">
        <h2>Debris Images</h2>
        <div className="image-gallery">
          {imageUrls.map((url, index) => (
            <img key={index} src={url} alt={`Image ${index + 1}`} className="image-item" />
          ))}
        </div>
      </div>
    </div>
  );
}

export default App;
