import React, { useState, useEffect } from 'react';
import { initializeApp } from 'firebase/app';
import { getDatabase, ref, onValue, set } from 'firebase/database';
import { getAuth, signInWithEmailAndPassword, signOut, onAuthStateChanged } from 'firebase/auth';
import './App.css';

// Firebase configuration
const firebaseConfig = {
  apiKey: "AIzaSyAUjjIy9Sd5r8T5nxIinHbcseCvNHKKag4",
  authDomain: "schoolbus-led.firebaseapp.com",
  databaseURL: "https://schoolbus-led-default-rtdb.firebaseio.com",
  projectId: "schoolbus-led",
  storageBucket: "schoolbus-led.appspot.com",
  messagingSenderId: "1056264797823",
  appId: "1:1056264797823:web:ab123cd456ef7890ghij" // Replace with your app ID from Firebase project settings
};

// Initialize Firebase
const app = initializeApp(firebaseConfig);
const database = getDatabase(app);
const auth = getAuth(app);

function App() {
  const [user, setUser] = useState(null); // State to track logged-in user
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [ledStatus, setLedStatus] = useState('OFF');
  const [reqSucStatus, setReqSucStatus] = useState('NO');

  useEffect(() => {
    // Listen for authentication state changes
    const unsubscribeAuth = onAuthStateChanged(auth, (user) => {
      if (user) {
        setUser(user);
      } else {
        setUser(null);
      }
    });

    return () => unsubscribeAuth();
  }, []);

  useEffect(() => {
    if (user) {
      const ledRef = ref(database, 'req-led1');
      const reqSucRef = ref(database, 'req-suc');

      const ledUnsubscribe = onValue(ledRef, (snapshot) => {
        const status = snapshot.val();
        setLedStatus(status);
      });

      const reqSucUnsubscribe = onValue(reqSucRef, (snapshot) => {
        const status = snapshot.val();
        setReqSucStatus(status);
      });

      return () => {
        ledUnsubscribe();
        reqSucUnsubscribe();
      };
    }
  }, [user]);

  const handleLogin = (e) => {
    e.preventDefault();
    signInWithEmailAndPassword(auth, email, password)
      .then((userCredential) => {
        setUser(userCredential.user);
        setError('');
      })
      .catch((error) => {
        setError('Login failed. Please check your credentials.');
      });
  };

  const handleLogout = () => {
    signOut(auth)
      .then(() => {
        setUser(null);
      })
      .catch((error) => {
        console.error("Logout error:", error);
      });
  };

  const toggleLed = () => {
    const newStatus = ledStatus === 'ON' ? 'OFF' : 'ON';
    set(ref(database, 'req-led1'), newStatus);
    set(ref(database, 'req-suc'), 'NO');
  };

  if (!user) {
    return (
      <div className="login-container">
        <h1>Login</h1>
        <form onSubmit={handleLogin}>
          <input
            type="email"
            placeholder="Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
          <button type="submit">Login</button>
        </form>
        {error && <p className="error">{error}</p>}
      </div>
    );
  }

  return (
    <div className="container">
      <h1 className="status-text">LED is {ledStatus}</h1>
      <h2 className="status-text">AUTHORIZED is {reqSucStatus}</h2>
      <div 
        className={`switch ${ledStatus === 'ON' ? 'switch-on' : 'switch-off'}`} 
        onClick={toggleLed}
      >
        <div className={`circle ${ledStatus === 'ON' ? 'circle-on' : 'circle-off'}`} />
      </div>
      <button className="logout-button" onClick={handleLogout}>Logout</button>
    </div>
  );
}

export default App;
