import React, { useEffect, useState } from 'react';
import { initializeApp } from 'firebase/app';
import { getDatabase, ref, onValue } from 'firebase/database';

// Firebase configuration
const firebaseConfig = {
  apiKey: "AIzaSyAUjjIy9Sd5r8T5nxIinHbcseCvNHKKag4",
  authDomain: "schoolbus-led.firebaseapp.com",
  databaseURL: "https://schoolbus-led-default-rtdb.firebaseio.com",
  projectId: "schoolbus-led",
  storageBucket: "schoolbus-led.appspot.com",
  messagingSenderId: "1056264797823",
  appId: "YOUR_APP_ID" // Replace with your app ID from Firebase project settings
};

// Initialize Firebase
const app = initializeApp(firebaseConfig);
const database = getDatabase(app);

const App = () => {
  const [statuses, setStatuses] = useState({});

  useEffect(() => {
    const authRef = ref(database, 'rfid-status');

    // Listen for value changes in Firebase
    const unsubscribe = onValue(authRef, (snapshot) => {
      const data = snapshot.val();
      setStatuses(data || {});
    });

    // Handle storage event for other tabs
    const handleStorageChange = (event) => {
      if (event.key === 'rfid-auth-status') {
        const updatedData = JSON.parse(event.newValue || '{}');
        setStatuses(updatedData);
      }
    };

    window.addEventListener('storage', handleStorageChange);

    // Cleanup on component unmount
    return () => {
      unsubscribe();
      window.removeEventListener('storage', handleStorageChange);
    };
  }, []);

  return (
    <div className="App">
      <h1>RFID Authentication Status</h1>
      <ul>
        {Object.entries(statuses).map(([uid, status]) => (
          <li key={uid}>
            UID: {uid} - Status: {status}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default App;
