import React, { useState, useEffect } from 'react';
import { initializeApp } from 'firebase/app';
import { getDatabase, ref, onValue, set } from 'firebase/database';
import { getAuth, signInWithEmailAndPassword, signOut, onAuthStateChanged } from 'firebase/auth';
import 'bootstrap/dist/css/bootstrap.min.css'; // Import Bootstrap
import './App.css'; // Custom styles

// Firebase configuration
const firebaseConfig = {
  apiKey: "AIzaSyAUjjIy9Sd5r8T5nxIinHbcseCvNHKKag4",
  authDomain: "schoolbus-led.firebaseapp.com",
  databaseURL: "https://schoolbus-led-default-rtdb.firebaseio.com",
  projectId: "schoolbus-led",
  storageBucket: "schoolbus-led.appspot.com",
  messagingSenderId: "1056264797823",
  appId: "1:1056264797823:web:ab123cd456ef7890ghij"
};

// Initialize Firebase
const app = initializeApp(firebaseConfig);
const database = getDatabase(app);
const auth = getAuth(app);

function App() {
  const [user, setUser] = useState(null);
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [ledStatus, setLedStatus] = useState('OFF');
  const [reqSucStatus, setReqSucStatus] = useState('NO');

  // Monitor authentication state
  useEffect(() => {
    const unsubscribeAuth = onAuthStateChanged(auth, (user) => {
      if (user) {
        setUser(user);
      } else {
        setUser(null);
      }
    });

    return () => unsubscribeAuth();
  }, []);

  // LED status listener
  useEffect(() => {
    if (user) {
      const ledRef = ref(database, 'req-led1');
      const reqSucRef = ref(database, 'req-suc');

      const ledUnsubscribe = onValue(ledRef, (snapshot) => {
        const status = snapshot.val();
        setLedStatus(status);
      });

      const reqSucUnsubscribe = onValue(reqSucRef, (snapshot) => {
        const status = snapshot.val();
        setReqSucStatus(status);
      });

      return () => {
        ledUnsubscribe();
        reqSucUnsubscribe();
      };
    }
  }, [user]);

  // Handle login
  const handleLogin = (e) => {
    e.preventDefault();
    signInWithEmailAndPassword(auth, email, password)
      .then((userCredential) => {
        setUser(userCredential.user);
        setError('');
      })
      .catch((error) => {
        setError(error.message);
      });
  };

  // Handle logout
  const handleLogout = () => {
    signOut(auth).then(() => {
      setUser(null);
    });
  };

  // Toggle LED status
  const toggleLed = () => {
    const newStatus = ledStatus === 'ON' ? 'OFF' : 'ON';
    set(ref(database, 'req-led1'), newStatus);
    set(ref(database, 'req-suc'), 'NO');
  };

  // Render login form if not authenticated
  if (!user) {
    return (
      <div className="d-flex justify-content-center align-items-center vh-100">
        <div className="card p-4" style={{ width: '400px' }}>
          <h3 className="text-center mb-3">Login</h3>
          {error && <div className="alert alert-danger">{error}</div>}
          <form onSubmit={handleLogin}>
            <div className="mb-3">
              <label htmlFor="email" className="form-label">Email</label>
              <input
                type="email"
                className="form-control"
                id="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                required
              />
            </div>
            <div className="mb-3">
              <label htmlFor="password" className="form-label">Password</label>
              <input
                type="password"
                className="form-control"
                id="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
              />
            </div>
            <button type="submit" className="btn btn-primary w-100">Login</button>
          </form>
        </div>
      </div>
    );
  }

  // Render dashboard if authenticated
  return (
    <div className="container text-center mt-5">
      <h1 className="mb-4">LED Control Panel</h1>
      <h2 className="status-text">LED is {ledStatus}</h2>
      <h2 className="status-text">AUTHORIZED is {reqSucStatus}</h2>
      <div
        className={`switch ${ledStatus === 'ON' ? 'switch-on' : 'switch-off'}`}
        onClick={toggleLed}
        style={{
          display: 'inline-block',
          padding: '10px',
          cursor: 'pointer',
          backgroundColor: ledStatus === 'ON' ? 'green' : 'red',
          borderRadius: '25px',
        }}
      >
        <div
          className={`circle ${ledStatus === 'ON' ? 'circle-on' : 'circle-off'}`}
          style={{
            width: '25px',
            height: '25px',
            backgroundColor: 'white',
            borderRadius: '50%',
            transition: '0.3s',
            transform: ledStatus === 'ON' ? 'translateX(30px)' : 'translateX(0)',
          }}
        />
      </div>
      <br /><br />
      <button className="btn btn-danger mt-3" onClick={handleLogout}>Logout</button>
    </div>
  );
}

export default App;
