import React, { useRef, useState, useEffect } from 'react';
import { initializeApp } from 'firebase/app';
import { getDatabase, ref, onValue, set } from 'firebase/database';
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';

// Firebase configuration
const firebaseConfig = {
  apiKey: "AIzaSyAUjjIy9Sd5r8T5nxIinHbcseCvNHKKag4",
  authDomain: "schoolbus-led.firebaseapp.com",
  databaseURL: "https://schoolbus-led-default-rtdb.firebaseio.com",
  projectId: "schoolbus-led",
  storageBucket: "schoolbus-led.appspot.com",
  messagingSenderId: "1056264797823",
  appId: "YOUR_APP_ID" // Replace with your app ID from Firebase project settings
};

// Initialize Firebase
const app = initializeApp(firebaseConfig);
const database = getDatabase(app);

const userPaths = {
  "member1@gmail.com": {
    rfidReaderPath: 'rfid-reader',
    path: 'find-aisle'
  },
  "member2@gmail.com": {
    rfidReaderPath: 'rfid-reader2',
    path: 'find-aisle2'
  }
};

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [username, setUsername] = useState('');
  const [startingAisle, setStartingAisle] = useState('');
  const [selectedItem, setSelectedItem] = useState('');
  const [showDropdown, setShowDropdown] = useState(false);
  const [showOptions, setShowOptions] = useState(false); // New state for showing options
  const canvasRef = useRef(null);
  const aisleRefs = useRef({});
  const [disabledItems, setDisabledItems] = useState(new Set());

  const handleLogin = (e) => {
    e.preventDefault();
    const formData = new FormData(e.target);
    const username = formData.get('username');
    setIsLoggedIn(true);
    setUsername(username);

    const userPath = userPaths[username];
    if (userPath) {
      const userRef = ref(database, 'current-user');
      set(userRef, username);
    }
  };

  const handleLogout = () => {
    setIsLoggedIn(false);
    setUsername('');

    const userRef = ref(database, 'current-user');
    set(userRef, '');
  };

  useEffect(() => {
    if (isLoggedIn) {
      const userPath = userPaths[username];
      if (userPath) {
        const rfidRef = ref(database, userPath.rfidReaderPath);

        const rfidUnsubscribe = onValue(rfidRef, (snapshot) => {
          const readerData = snapshot.val();
          if (readerData) {
            setStartingAisle(readerData);
            setShowDropdown(true);
          }
        });

        return () => {
          rfidUnsubscribe();
        };
      }
    }
  }, [isLoggedIn, username]);

  useEffect(() => {
    if (startingAisle && selectedItem && canvasRef.current) {
      drawPath();
    } else {
      clearPath();
    }
  }, [startingAisle, selectedItem]);

  useEffect(() => {
    // Function to handle Firebase updates
    const updateAislePath = (path) => {
      const pathRef = ref(database, path);

      // Set the path to 'ON' for 4 seconds
      set(pathRef, 'ON');

      // After 4 seconds, set it to 'OFF' for 1 second
      setTimeout(() => {
        set(pathRef, 'OFF');

        // After 1 second, reset the path to 'IDLE' (or any neutral state)
        setTimeout(() => {
          set(pathRef, 'IDLE'); // or 'IDLE'
        }, 0.0001); // Delay before resetting to 'IDLE'
      }, 4000); // ON for 4 seconds
    };

    if (selectedItem) {
      let aislePath;
      switch (selectedItem) {
        case '1.1': aislePath = 'aisle1_1'; break;
        case '1.2': aislePath = 'aisle1_2'; break;
        case '1.3': aislePath = 'aisle1_3'; break;
        case '2.1': aislePath = 'aisle2_1'; break;
        case '2.2': aislePath = 'aisle2_2'; break;
        case '2.3': aislePath = 'aisle2_3'; break;
        case '3.1': aislePath = 'aisle3_1'; break;
        case '3.2': aislePath = 'aisle3_2'; break;
        case '3.3': aislePath = 'aisle3_3'; break;
        default: aislePath = ''; break;
      }
      if (aislePath) {
        console.log(`${selectedItem} selected`);
        updateAislePath(aislePath);
        setDisabledItems(prev => new Set(prev.add(selectedItem)));
      }
    }
  }, [selectedItem]);

  const drawPath = () => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    const supermarket = document.getElementById('supermarket');
    const aisleElement = aisleRefs.current[startingAisle];
    const itemElement = document.getElementById(`item${selectedItem.replace('.', '-')}`);

    if (!canvas || !ctx || !aisleElement || !itemElement) return;

    ctx.clearRect(0, 0, canvas.width, canvas.height);

    const aisleRect = aisleElement.getBoundingClientRect();
    const itemRect = itemElement.getBoundingClientRect();
    const supermarketRect = supermarket.getBoundingClientRect();

    const aisleX = aisleRect.left - supermarketRect.left + (aisleElement.offsetWidth / 2);
    const aisleY = aisleRect.top - supermarketRect.top + (aisleElement.offsetHeight / 2);
    const itemX = itemRect.left - supermarketRect.left + (itemElement.offsetWidth / 2);
    const itemY = itemRect.top - supermarketRect.top + (itemElement.offsetHeight / 2);

    ctx.beginPath();
    ctx.moveTo(aisleX, aisleY);
    ctx.lineTo(itemX, itemY);
    ctx.strokeStyle = '#e74c3c';
    ctx.lineWidth = 3;
    ctx.stroke();
  };

  const clearPath = () => {
    const canvas = canvasRef.current;
    const ctx = canvas?.getContext('2d');

    if (!canvas || !ctx) return;

    ctx.clearRect(0, 0, canvas.width, canvas.height);
  };

  const handleFindItem = () => {
    if (selectedItem) {
      const aislePath = `aisle${selectedItem.replace('.', '_')}`;
      const pathRef = ref(database, aislePath);

      // Set the aisle to 'OFF'
      set(pathRef, 'OFF').then(() => {
        // Draw the path after setting the value to 'OFF'
        drawPath();
      }).catch((error) => {
        console.error("Error updating Firebase:", error);
      });
    }
  };

  const handleReadStartingAisle = () => {
    const userPath = userPaths[username];
    if (userPath) {
      const rfidRef = ref(database, userPath.rfidReaderPath);
      onValue(rfidRef, (snapshot) => {
        const readerData = snapshot.val();
        if (readerData) {
          setStartingAisle(readerData);
          setShowDropdown(false);

          const pathRef = ref(database, userPath.path);
          set(pathRef, 'YES');
        }
      });
    }
  };

  const toggleOptionsVisibility = () => {
    setShowOptions(prev => !prev);
  };

  return (
    <div className="App">
      {!isLoggedIn ? (
        <div className="d-flex justify-content-center align-items-center min-vh-100">
          <div style={{ maxWidth: '400px', width: '100%' }}>
            <h2 className="text-center mb-4">Login</h2>
            <form onSubmit={handleLogin}>
              <div className="mb-3">
                <label htmlFor="login-username" className="form-label">Username</label>
                <input type="text" className="form-control" id="login-username" name="username" required />
              </div>
              <div className="mb-3">
                <label htmlFor="login-password" className="form-label">Password</label>
                <input type="password" className="form-control" id="login-password" name="password" required />
              </div>
              <button type="submit" className="btn btn-primary w-100">Login</button>
            </form>
          </div>
        </div>
      ) : (
        <div>
          <nav className="navbar navbar-light bg-light">
            <span className="navbar-brand mb-0 h1">Welcome, {username}</span>
            <button className="btn btn-danger" onClick={handleLogout}>Logout</button>
          </nav>
          <div className="container my-4">
            <h3>Find the Item</h3>
            <div id="supermarket" className="position-relative">
              <div id="aisle1_1" ref={el => (aisleRefs.current['1.1'] = el)} className="aisle" />
              <div id="aisle1_2" ref={el => (aisleRefs.current['1.2'] = el)} className="aisle" />
              <div id="aisle1_3" ref={el => (aisleRefs.current['1.3'] = el)} className="aisle" />
              <div id="aisle2_1" ref={el => (aisleRefs.current['2.1'] = el)} className="aisle" />
              <div id="aisle2_2" ref={el => (aisleRefs.current['2.2'] = el)} className="aisle" />
              <div id="aisle2_3" ref={el => (aisleRefs.current['2.3'] = el)} className="aisle" />
              <div id="aisle3_1" ref={el => (aisleRefs.current['3.1'] = el)} className="aisle" />
              <div id="aisle3_2" ref={el => (aisleRefs.current['3.2'] = el)} className="aisle" />
              <div id="aisle3_3" ref={el => (aisleRefs.current['3.3'] = el)} className="aisle" />
              <canvas ref={canvasRef} className="position-absolute top-0 start-0 w-100 h-100" />
            </div>
            <button className="btn btn-primary my-3" onClick={toggleOptionsVisibility}>
              {showOptions ? 'Hide Options' : 'Show Options'}
            </button>
            {showOptions && (
              <div className="btn-group-vertical">
                <button
                  id="item1-1"
                  className="btn btn-secondary"
                  onClick={() => setSelectedItem('1.1')}
                  disabled={disabledItems.has('1.1')}
                >
                  Item 1.1
                </button>
                <button
                  id="item1-2"
                  className="btn btn-secondary"
                  onClick={() => setSelectedItem('1.2')}
                  disabled={disabledItems.has('1.2')}
                >
                  Item 1.2
                </button>
                <button
                  id="item1-3"
                  className="btn btn-secondary"
                  onClick={() => setSelectedItem('1.3')}
                  disabled={disabledItems.has('1.3')}
                >
                  Item 1.3
                </button>
                <button
                  id="item2-1"
                  className="btn btn-secondary"
                  onClick={() => setSelectedItem('2.1')}
                  disabled={disabledItems.has('2.1')}
                >
                  Item 2.1
                </button>
                <button
                  id="item2-2"
                  className="btn btn-secondary"
                  onClick={() => setSelectedItem('2.2')}
                  disabled={disabledItems.has('2.2')}
                >
                  Item 2.2
                </button>
                <button
                  id="item2-3"
                  className="btn btn-secondary"
                  onClick={() => setSelectedItem('2.3')}
                  disabled={disabledItems.has('2.3')}
                >
                  Item 2.3
                </button>
                <button
                  id="item3-1"
                  className="btn btn-secondary"
                  onClick={() => setSelectedItem('3.1')}
                  disabled={disabledItems.has('3.1')}
                >
                  Item 3.1
                </button>
                <button
                  id="item3-2"
                  className="btn btn-secondary"
                  onClick={() => setSelectedItem('3.2')}
                  disabled={disabledItems.has('3.2')}
                >
                  Item 3.2
                </button>
                <button
                  id="item3-3"
                  className="btn btn-secondary"
                  onClick={() => setSelectedItem('3.3')}
                  disabled={disabledItems.has('3.3')}
                >
                  Item 3.3
                </button>
              </div>
            )}
            <button className="btn btn-success mt-3" onClick={handleFindItem}>Find Item</button>
            <button className="btn btn-info mt-3" onClick={handleReadStartingAisle}>Read Starting Aisle</button>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
