// Import React and Firebase modules
import React, { useState, useEffect } from 'react';
import { initializeApp } from 'firebase/app';
import { getDatabase, ref, set, onValue } from 'firebase/database';

// Firebase configuration
const firebaseConfig = {
  apiKey: "AIzaSyAUjjIy9Sd5r8T5nxIinHbcseCvNHKKag4",
  authDomain: "schoolbus-led.firebaseapp.com",
  databaseURL: "https://schoolbus-led-default-rtdb.firebaseio.com",
  projectId: "schoolbus-led",
  storageBucket: "schoolbus-led.appspot.com",
  messagingSenderId: "1056264797823",
  appId: "YOUR_APP_ID" // Replace with your app ID from Firebase project settings
};

// Initialize Firebase
const app = initializeApp(firebaseConfig);
const database = getDatabase(app);

const App = () => {
  const [ledStatus, setLedStatus] = useState('OFF');

  // Reference to Firebase Realtime Database for the 'test-led1' path
  const ledRef = ref(database, 'test-led1');

  // Fetch current status from Firebase on component mount
  useEffect(() => {
    onValue(ledRef, (snapshot) => {
      const status = snapshot.val();
      if (status) {
        setLedStatus(status);
      }
    });

    // Cleanup listener on component unmount
    return () => ledRef.off();
  }, [ledRef]);

  // Toggle function to update Firebase
  const toggleLed = () => {
    const newStatus = ledStatus === 'ON' ? 'OFF' : 'ON';
    set(ledRef, newStatus);
  };

  return (
    <div style={{ textAlign: 'center', marginTop: '50px' }}>
      <button onClick={toggleLed} style={{ fontSize: '20px', padding: '10px 20px' }}>
        {ledStatus === 'ON' ? 'Turn OFF' : 'Turn ON'}
      </button>
      <p style={{ fontSize: '24px', marginTop: '20px' }}>LED Status: {ledStatus}</p>
    </div>
  );
};

export default App;
