import React, { useState, useEffect } from 'react';
import { initializeApp } from 'firebase/app';
import { getDatabase, ref, set, onValue } from 'firebase/database';

// Firebase configuration
const firebaseConfig = {
  apiKey: "AIzaSyAUjjIy9Sd5r8T5nxIinHbcseCvNHKKag4",
  authDomain: "schoolbus-led.firebaseapp.com",
  databaseURL: "https://schoolbus-led-default-rtdb.firebaseio.com",
  projectId: "schoolbus-led",
  storageBucket: "schoolbus-led.appspot.com",
  messagingSenderId: "1056264797823",
  appId: "YOUR_APP_ID" // Replace with your app ID from Firebase project settings
};

// Initialize Firebase
const app = initializeApp(firebaseConfig);
const database = getDatabase(app);

// Firebase References
const ledRef = ref(database, '/test-led');
const rfidStatusRef = ref(database, '/rfid-status');

const App = () => {
  const [ledStatus, setLedStatus] = useState(false); // Track LED state
  const [rfidPrompt, setRfidPrompt] = useState(false); // Track if RFID prompt is needed
  const [authorized, setAuthorized] = useState(false); // Track RFID authorization

  // Function to toggle the LED
  const toggleLed = (status) => {
    setRfidPrompt(true); // Show RFID prompt to the user

    // Update Firebase to show that RFID scan is needed
    set(rfidStatusRef, 'scan'); 

    // Check RFID authorization after some delay
    const checkAuthorization = setInterval(() => {
      onValue(rfidStatusRef, (snapshot) => {
        const rfidStatus = snapshot.val();
        if (rfidStatus === 'authorized') {
          // If RFID is authorized, update LED status
          set(ledRef, status ? 'ON' : 'OFF'); // Update Firebase with new LED status
          setLedStatus(status);
          setRfidPrompt(false); // Hide RFID prompt
          setAuthorized(true);
          clearInterval(checkAuthorization); // Stop checking
        } else if (rfidStatus === 'not_authorized') {
          alert('RFID Not Authorized');
          setRfidPrompt(false); // Hide RFID prompt
          setAuthorized(false);
          clearInterval(checkAuthorization); // Stop checking
        }
      });
    }, 1000); // Check every second
  };

  useEffect(() => {
    // Get initial LED status from Firebase
    onValue(ledRef, (snapshot) => {
      const data = snapshot.val();
      setLedStatus(data === 'ON');
    });
  }, []);

  return (
    <div style={styles.container}>
      <h1>RFID-LED Control Panel</h1>

      <button onClick={() => toggleLed(!ledStatus)} style={styles.button}>
        {ledStatus ? 'Turn LED Off' : 'Turn LED On'}
      </button>

      {rfidPrompt && (
        <div style={styles.rfidPrompt}>
          <p>Please scan your RFID card...</p>
          {authorized ? (
            <p style={{ color: 'green' }}>RFID Authorized!</p>
          ) : (
            <p style={{ color: 'red' }}>Waiting for RFID authorization...</p>
          )}
        </div>
      )}
    </div>
  );
};

// Simple CSS-in-JS styles
const styles = {
  container: {
    textAlign: 'center',
    padding: '50px',
    fontFamily: 'Arial, sans-serif',
  },
  button: {
    padding: '10px 20px',
    fontSize: '18px',
    cursor: 'pointer',
  },
  rfidPrompt: {
    marginTop: '20px',
  },
};

export default App;
