import React, { useRef, useState, useEffect } from 'react';
import { initializeApp } from 'firebase/app';
import { getDatabase, ref, onValue, set } from 'firebase/database';
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';

// Firebase configuration
const firebaseConfig = {
  apiKey: "AIzaSyAUjjIy9Sd5r8T5nxIinHbcseCvNHKKag4",
  authDomain: "schoolbus-led.firebaseapp.com",
  databaseURL: "https://schoolbus-led-default-rtdb.firebaseio.com",
  projectId: "schoolbus-led",
  storageBucket: "schoolbus-led.appspot.com",
  messagingSenderId: "1056264797823",
  appId: "YOUR_APP_ID" // Replace with your app ID from Firebase project settings
};

// Initialize Firebase
const app = initializeApp(firebaseConfig);
const database = getDatabase(app);

const userPaths = {
  "member1@gmail.com": {
    rfidReaderPath: 'rfid-reader',
    path: 'find-aisle'
  },
  "member2@gmail.com": {
    rfidReaderPath: 'rfid-reader2',
    path: 'find-aisle2'
  }
};

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [username, setUsername] = useState('');
  const [startingAisle, setStartingAisle] = useState('');
  const [selectedItem, setSelectedItem] = useState('');
  const [dropdownDisabled, setDropdownDisabled] = useState(false); // New state
  const [showDropdown, setShowDropdown] = useState(false);
  const canvasRef = useRef(null);
  const aisleRefs = useRef({});

  const handleLogin = (e) => {
    e.preventDefault();
    const formData = new FormData(e.target);
    const username = formData.get('username');
    setIsLoggedIn(true);
    setUsername(username);

    const userPath = userPaths[username];
    if (userPath) {
      const userRef = ref(database, 'current-user');
      set(userRef, username);
    }
  };

  const handleLogout = () => {
    setIsLoggedIn(false);
    setUsername('');

    const userRef = ref(database, 'current-user');
    set(userRef, '');
  };

  useEffect(() => {
    if (isLoggedIn) {
      const userPath = userPaths[username];
      if (userPath) {
        const rfidRef = ref(database, userPath.rfidReaderPath);

        const rfidUnsubscribe = onValue(rfidRef, (snapshot) => {
          const readerData = snapshot.val();
          if (readerData) {
            setStartingAisle(readerData);
            setShowDropdown(true);
          }
        });

        return () => {
          rfidUnsubscribe();
        };
      }
    }
  }, [isLoggedIn, username]);

  useEffect(() => {
    if (startingAisle && selectedItem && canvasRef.current) {
      drawPath();
    } else {
      clearPath();
    }
  }, [startingAisle, selectedItem]);

  useEffect(() => {
    // Function to handle Firebase updates
    const updateAislePath = (path) => {
      const pathRef = ref(database, path);

      // Set the path to 'ON' for 4 seconds
      set(pathRef, 'ON');

      // After 4 seconds, set it to 'OFF' for 1 second
      // After 1 second, reset the path to 'IDLE' (or any neutral state)
    };

    if (selectedItem === '1.1') {
      updateAislePath('aisle1_1');
    } else if (selectedItem === '1.2') {
      updateAislePath('aisle1_2');
    } else if (selectedItem === '1.3') {
      updateAislePath('aisle1_3');
    } else if (selectedItem === '2.1') {
      updateAislePath('aisle2_1');
    } else if (selectedItem === '2.2') {
      updateAislePath('aisle2_2');
    } else if (selectedItem === '2.3') {
      updateAislePath('aisle2_3');
    } else if (selectedItem === '3.1') {
      updateAislePath('aisle3_1');
    } else if (selectedItem === '3.2') {
      updateAislePath('aisle3_2');
    } else if (selectedItem === '3.3') {
      updateAislePath('aisle3_3');
    }
  }, [selectedItem]);

  const drawPath = () => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    const supermarket = document.getElementById('supermarket');
    const aisleElement = aisleRefs.current[startingAisle];
    const itemElement = document.getElementById(`item${selectedItem.replace('.', '-')}`);

    if (!canvas || !ctx || !aisleElement || !itemElement) return;

    ctx.clearRect(0, 0, canvas.width, canvas.height);

    const aisleRect = aisleElement.getBoundingClientRect();
    const itemRect = itemElement.getBoundingClientRect();
    const supermarketRect = supermarket.getBoundingClientRect();

    const aisleX = aisleRect.left - supermarketRect.left + (aisleElement.offsetWidth / 2);
    const aisleY = aisleRect.top - supermarketRect.top + (aisleElement.offsetHeight / 2);
    const itemX = itemRect.left - supermarketRect.left + (itemElement.offsetWidth / 2);
    const itemY = itemRect.top - supermarketRect.top + (itemElement.offsetHeight / 2);

    ctx.beginPath();
    ctx.moveTo(aisleX, aisleY);
    ctx.lineTo(itemX, itemY);
    ctx.strokeStyle = '#e74c3c';
    ctx.lineWidth = 3;
    ctx.stroke();
  };

  const clearPath = () => {
    const canvas = canvasRef.current;
    const ctx = canvas?.getContext('2d');

    if (!canvas || !ctx) return;

    ctx.clearRect(0, 0, canvas.width, canvas.height);
  };

  const handleFindItem = () => {
    if (selectedItem) {
      const aislePath = `aisle${selectedItem.replace('.', '_')}`;
      const pathRef = ref(database, aislePath);

      // Set the aisle to 'OFF'
      set(pathRef, 'OFF').then(() => {
        // Draw the path after setting the value to 'OFF'
        drawPath();
        // Disable the dropdown after finding the item
        setDropdownDisabled(true);
      }).catch((error) => {
        console.error("Error updating Firebase:", error);
      });
    }
  };

  const handleReadStartingAisle = () => {
    const userPath = userPaths[username];
    if (userPath) {
      const rfidRef = ref(database, userPath.rfidReaderPath);
      onValue(rfidRef, (snapshot) => {
        const readerData = snapshot.val();
        if (readerData) {
          setStartingAisle(readerData);
          setShowDropdown(false);

          const pathRef = ref(database, userPath.path);
          set(pathRef, 'YES');
        }
      });
    }
  };

  return (
    <div className="App">
      {!isLoggedIn ? (
        <div className="d-flex justify-content-center align-items-center min-vh-100">
          <div style={{ maxWidth: '400px', width: '100%' }}>
            <h2 className="text-center mb-4">Login</h2>
            <form onSubmit={handleLogin}>
              <div className="mb-3">
                <label htmlFor="login-username" className="form-label">Username</label>
                <input type="text" className="form-control" id="login-username" name="username" required />
              </div>
              <div className="mb-3">
                <label htmlFor="login-password" className="form-label">Password</label>
                <input type="password" className="form-control" id="login-password" name="password" required />
              </div>
              <button type="submit" className="btn btn-primary w-100">Login</button>
            </form>
          </div>
        </div>
      ) : (
        <div>
          <nav className="navbar navbar-expand-lg navbar-light bg-light">
            <div className="container-fluid">
              <a className="navbar-brand" href="#">Welcome, {username}</a>
              <button className="btn btn-outline-danger" onClick={handleLogout}>Logout</button>
            </div>
          </nav>
          <div className="container mt-4">
            <div className="d-flex justify-content-center">
              <button className="btn btn-primary" onClick={handleReadStartingAisle}>
                {startingAisle ? "Item found, Click here for next item" : "Read Starting Aisle"}
              </button>
            </div>
            {showDropdown && (
              <div className="mt-4">
                <div className="d-flex justify-content-center">
                  <select
                    className="form-select w-50"
                    onChange={(e) => setSelectedItem(e.target.value)}
                    disabled={dropdownDisabled} // Control visibility based on state
                  >
                    <option value="">Select Item</option>
                    <option value="1.1">Item 1.1</option>
                    <option value="1.2">Item 1.2</option>
                    <option value="1.3">Item 1.3</option>
                    <option value="2.1">Item 2.1</option>
                    <option value="2.2">Item 2.2</option>
                    <option value="2.3">Item 2.3</option>
                    <option value="3.1">Item 3.1</option>
                    <option value="3.2">Item 3.2</option>
                    <option value="3.3">Item 3.3</option>
                  </select>
                </div>
                <div className="d-flex justify-content-center mt-2">
                  <button className="btn btn-primary" onClick={handleFindItem}>
                    Find Item
                  </button>
                </div>
              </div>
            )}
            <div id="supermarket" className="position-relative mt-4">
              <canvas
                ref={canvasRef}
                width={800}
                height={600}
                style={{ border: '1px solid black', position: 'absolute', top: 0, left: 0 }}
              ></canvas>
              <div ref={(el) => (aisleRefs.current['Aisle 1'] = el)} id="aisle1" className="position-absolute" style={{ top: '100px', left: '200px' }}>Aisle 1</div>
              <div ref={(el) => (aisleRefs.current['Aisle 2'] = el)} id="aisle2" className="position-absolute" style={{ top: '200px', left: '400px' }}>Aisle 2</div>
              <div ref={(el) => (aisleRefs.current['Aisle 3'] = el)} id="aisle3" className="position-absolute" style={{ top: '300px', left: '600px' }}>Aisle 3</div>
              <div id="item1-1" className="position-absolute" style={{ top: '150px', left: '250px' }}>Item 1.1</div>
              <div id="item1-2" className="position-absolute" style={{ top: '160px', left: '270px' }}>Item 1.2</div>
              <div id="item1-3" className="position-absolute" style={{ top: '170px', left: '290px' }}>Item 1.3</div>
              <div id="item2-1" className="position-absolute" style={{ top: '250px', left: '450px' }}>Item 2.1</div>
              <div id="item2-2" className="position-absolute" style={{ top: '260px', left: '470px' }}>Item 2.2</div>
              <div id="item2-3" className="position-absolute" style={{ top: '270px', left: '490px' }}>Item 2.3</div>
              <div id="item3-1" className="position-absolute" style={{ top: '350px', left: '650px' }}>Item 3.1</div>
              <div id="item3-2" className="position-absolute" style={{ top: '360px', left: '670px' }}>Item 3.2</div>
              <div id="item3-3" className="position-absolute" style={{ top: '370px', left: '690px' }}>Item 3.3</div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
