import React, { useState, useEffect } from 'react';
import firebase from 'firebase/app';

const ToggleButton = () => {
  const [ledStatus, setLedStatus] = useState('OFF');

  // Fetch current status from Firebase on component mount
  useEffect(() => {
    const ledRef = firebase.database().ref('test-led1');
    ledRef.on('value', (snapshot) => {
      const status = snapshot.val();
      setLedStatus(status);
    });

    // Cleanup listener on component unmount
    return () => ledRef.off();
  }, []);

  // Toggle function to update Firebase
  const toggleLed = () => {
    const newStatus = ledStatus === 'ON' ? 'OFF' : 'ON';
    firebase.database().ref('test-led1').set(newStatus);
  };

  return (
    <div>
      <button onClick={toggleLed}>
        {ledStatus === 'ON' ? 'Turn OFF' : 'Turn ON'}
      </button>
      <p>LED Status: {ledStatus}</p>
    </div>
  );
};

export default ToggleButton;
