import React, { useRef, useEffect, useState } from 'react';
import { initializeApp } from 'firebase/app';
import { getDatabase, ref, onValue, set } from 'firebase/database';
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';

// Firebase configuration
const firebaseConfig = {
  apiKey: "AIzaSyAUjjIy9Sd5r8T5nxIinHbcseCvNHKKag4",
  authDomain: "schoolbus-led.firebaseapp.com",
  databaseURL: "https://schoolbus-led-default-rtdb.firebaseio.com",
  projectId: "schoolbus-led",
  storageBucket: "schoolbus-led.appspot.com",
  messagingSenderId: "1056264797823",
  appId: "YOUR_APP_ID" // Replace with your app ID from Firebase project settings
};

// Initialize Firebase
const app = initializeApp(firebaseConfig);
const database = getDatabase(app);

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [username, setUsername] = useState('');
  const [startingAisle, setStartingAisle] = useState('');
  const [selectedItem, setSelectedItem] = useState('');
  const [showDropdown, setShowDropdown] = useState(false);
  const canvasRef = useRef(null);
  const aisleRefs = useRef({});

  const handleLogin = (e) => {
    e.preventDefault();
    const formData = new FormData(e.target);
    const username = formData.get('username');
    setIsLoggedIn(true);
    setUsername(username);

    // Set the current user in Firebase
    const userRef = ref(database, `users/${username}`);
    set(userRef, {
      loggedIn: true,
      startingAisle: '',
      selectedItem: ''
    });
  };

  const handleLogout = () => {
    // Clear the current user from Firebase
    const userRef = ref(database, `users/${username}`);
    set(userRef, {
      loggedIn: false,
      startingAisle: '',
      selectedItem: ''
    });

    setIsLoggedIn(false);
    setUsername('');
  };

  useEffect(() => {
    if (isLoggedIn) {
      const rfidPath = username === 'member1@gmail.com' ? 'rfid-reader' : 'rfid-reader2';
      const userPath = username === 'member1@gmail.com' ? 'users/member1@gmail.com' : 'users/member2@gmail.com';

      const userRef = ref(database, `${userPath}/startingAisle`);
      const rfidRef = ref(database, rfidPath);

      const rfidUnsubscribe = onValue(rfidRef, (snapshot) => {
        const readerData = snapshot.val();
        if (readerData) {
          setStartingAisle(readerData);
          setShowDropdown(true); // Show the dropdown when data is loaded

          // Update startingAisle in user data
          set(ref(database, `${userPath}/startingAisle`), readerData);
        }
      });

      const userAisleUnsubscribe = onValue(userRef, (snapshot) => {
        const userData = snapshot.val();
        if (userData && userData.startingAisle) {
          setStartingAisle(userData.startingAisle);
          setSelectedItem(userData.selectedItem || '');
          setShowDropdown(true);
        }
      });

      return () => {
        rfidUnsubscribe();
        userAisleUnsubscribe();
      };
    }
  }, [isLoggedIn, username]);

  useEffect(() => {
    if (startingAisle && selectedItem && canvasRef.current) {
      drawPath();
    } else {
      clearPath();
    }
  }, [startingAisle, selectedItem, canvasRef.current]);

  const drawPath = () => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    const supermarket = document.getElementById('supermarket');
    const aisleElement = aisleRefs.current[startingAisle];
    const itemElement = document.getElementById(`item${selectedItem.replace('.', '-')}`);

    if (!canvas || !ctx || !aisleElement || !itemElement) return;

    ctx.clearRect(0, 0, canvas.width, canvas.height);

    const aisleRect = aisleElement.getBoundingClientRect();
    const itemRect = itemElement.getBoundingClientRect();
    const supermarketRect = supermarket.getBoundingClientRect();

    const aisleX = aisleRect.left - supermarketRect.left + (aisleElement.offsetWidth / 2);
    const aisleY = aisleRect.top - supermarketRect.top + (aisleElement.offsetHeight / 2);
    const itemX = itemRect.left - supermarketRect.left + (itemElement.offsetWidth / 2);
    const itemY = itemRect.top - supermarketRect.top + (itemElement.offsetHeight / 2);

    ctx.beginPath();
    ctx.moveTo(aisleX, aisleY);
    ctx.lineTo(itemX, itemY);
    ctx.strokeStyle = '#e74c3c';
    ctx.lineWidth = 3;
    ctx.stroke();
  };

  const clearPath = () => {
    const canvas = canvasRef.current;
    const ctx = canvas?.getContext('2d');

    if (!canvas || !ctx) return;

    ctx.clearRect(0, 0, canvas.width, canvas.height);
  };

  const handleReadStartingAisle = () => {
    const rfidPath = username === 'member1@gmail.com' ? 'rfid-reader' : 'rfid-reader2';
    const pathRef = ref(database, 'find-aisle');

    onValue(ref(database, rfidPath), (snapshot) => {
      const readerData = snapshot.val();
      if (readerData) {
        setStartingAisle(readerData);
        setShowDropdown(false);

        // Update startingAisle in user data
        const userPath = username === 'member1@gmail.com' ? 'users/member1@gmail.com' : 'users/member2@gmail.com';
        set(ref(database, `${userPath}/startingAisle`), readerData);

        // Push "YES" to Firebase path
        set(pathRef, 'YES');
      }
    });
  };

  const handleSelectItem = (event) => {
    const selectedValue = event.target.value;
    setSelectedItem(selectedValue);

    // Update selectedItem in user data
    const userPath = username === 'member1@gmail.com' ? 'users/member1@gmail.com' : 'users/member2@gmail.com';
    set(ref(database, `${userPath}/selectedItem`), selectedValue);
  };

  return (
    <div className="App">
      {!isLoggedIn ? (
        <div className="d-flex justify-content-center align-items-center min-vh-100">
          <div style={{ maxWidth: '400px', width: '100%' }}>
            <h2 className="text-center mb-4">Login</h2>
            <form onSubmit={handleLogin}>
              <div className="mb-3">
                <label htmlFor="login-username" className="form-label">Username</label>
                <input type="text" className="form-control" id="login-username" name="username" required />
              </div>
              <div className="mb-3">
                <label htmlFor="login-password" className="form-label">Password</label>
                <input type="password" className="form-control" id="login-password" name="password" required />
              </div>
              <button type="submit" className="btn btn-primary w-100">Login</button>
            </form>
          </div>
        </div>
      ) : (
        <div>
          <button className="btn btn-danger" onClick={handleLogout}>Logout</button>
          <h2>Welcome, {username}</h2>
          <h2>Find Your Item</h2>
          <div>
            <label htmlFor="starting-aisle">Select Starting Aisle:</label>
            <div>
              <input
                type="text"
                id="starting-aisle"
                value={startingAisle}
                readOnly
                placeholder="Select Starting Aisle"
              />
              <button onClick={handleReadStartingAisle}>Read Starting Aisle</button>
            </div>
          </div>
          <div>
            <label htmlFor="item-selector">Select Item:</label>
            <select
              id="item-selector"
              value={selectedItem}
              onChange={handleSelectItem}
            >
              <option value="">Select an item</option>
              <option value="1.1">Aisle 1, Apples</option>
              <option value="1.2">Aisle 1, Bananas</option>
              <option value="1.3">Aisle 1, Oranges</option>
              <option value="2.1">Aisle 2, Bread</option>
              <option value="2.2">Aisle 2, Milk</option>
              <option value="2.3">Aisle 2, Cheese</option>
              <option value="3.1">Aisle 3, Shampoo</option>
              <option value="3.2">Aisle 3, Soap</option>
              <option value="3.3">Aisle 3, Toothpaste</option>
            </select>
          </div>
          <div id="supermarket" style={{ position: 'relative', width: '100%', height: '500px' }}>
            <div id="aisle1" ref={el => aisleRefs.current['1.1'] = el} className="aisle">Aisle 1</div>
            <div id="aisle2" ref={el => aisleRefs.current['2.1'] = el} className="aisle">Aisle 2</div>
            <div id="aisle3" ref={el => aisleRefs.current['3.1'] = el} className="aisle">Aisle 3</div>
            <div id="item1.1" className="item">Apples</div>
            <div id="item1.2" className="item">Bananas</div>
            <div id="item1.3" className="item">Oranges</div>
            <div id="item2.1" className="item">Bread</div>
            <div id="item2.2" className="item">Milk</div>
            <div id="item2.3" className="item">Cheese</div>
            <div id="item3.1" className="item">Shampoo</div>
            <div id="item3.2" className="item">Soap</div>
            <div id="item3.3" className="item">Toothpaste</div>
            <canvas ref={canvasRef} width="500" height="500" style={{ position: 'absolute', top: 0, left: 0, pointerEvents: 'none' }} />
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
